PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "migrations" ("id" integer primary key autoincrement not null, "migration" varchar not null, "batch" integer not null);
CREATE TABLE IF NOT EXISTS "password_reset_tokens" ("email" varchar not null, "token" varchar not null, "created_at" datetime, primary key ("email"));
CREATE TABLE IF NOT EXISTS "sessions" ("id" varchar not null, "user_id" integer, "ip_address" varchar, "user_agent" text, "payload" text not null, "last_activity" integer not null, primary key ("id"));
CREATE TABLE IF NOT EXISTS "cache" ("key" varchar not null, "value" text not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "cache_locks" ("key" varchar not null, "owner" varchar not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "jobs" ("id" integer primary key autoincrement not null, "queue" varchar not null, "payload" text not null, "attempts" integer not null, "reserved_at" integer, "available_at" integer not null, "created_at" integer not null);
CREATE TABLE IF NOT EXISTS "job_batches" ("id" varchar not null, "name" varchar not null, "total_jobs" integer not null, "pending_jobs" integer not null, "failed_jobs" integer not null, "failed_job_ids" text not null, "options" text, "cancelled_at" integer, "created_at" integer not null, "finished_at" integer, primary key ("id"));
CREATE TABLE IF NOT EXISTS "failed_jobs" ("id" integer primary key autoincrement not null, "uuid" varchar not null, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime not null default CURRENT_TIMESTAMP);
CREATE TABLE IF NOT EXISTS "permissions" ("id" integer primary key autoincrement not null, "name" varchar not null, "guard_name" varchar not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "roles" ("id" integer primary key autoincrement not null, "name" varchar not null, "guard_name" varchar not null, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "model_has_permissions" ("permission_id" integer not null, "model_type" varchar not null, "model_id" integer not null, foreign key("permission_id") references "permissions"("id") on delete cascade, primary key ("permission_id", "model_id", "model_type"));
CREATE TABLE IF NOT EXISTS "model_has_roles" ("role_id" integer not null, "model_type" varchar not null, "model_id" integer not null, foreign key("role_id") references "roles"("id") on delete cascade, primary key ("role_id", "model_id", "model_type"));
CREATE TABLE IF NOT EXISTS "role_has_permissions" ("permission_id" integer not null, "role_id" integer not null, foreign key("permission_id") references "permissions"("id") on delete cascade, foreign key("role_id") references "roles"("id") on delete cascade, primary key ("permission_id", "role_id"));
CREATE TABLE IF NOT EXISTS "suppliers" ("id" integer primary key autoincrement not null, "name" varchar not null, "responsible" varchar, "email" varchar, "phone" varchar, "address" varchar, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "bodegas" ("id" integer primary key autoincrement not null, "nombre" varchar not null, "direccion" varchar, "tipo" varchar check ("tipo" in ('almacen', 'fabrica')) not null default 'almacen', "encargado_id" integer, "created_at" datetime, "updated_at" datetime, foreign key("encargado_id") references "users"("id") on delete set null);
CREATE TABLE IF NOT EXISTS "movimiento_inventarios" ("id" integer primary key autoincrement not null, "bodega_id" integer not null, "articles_id" integer not null, "cantidad" integer not null, "tipo" varchar not null, "estado" varchar not null default 'pendiente_confirmacion', "observaciones" text, "creado_por_id" integer, "confirmado_por_id" integer, "confirmado_en" datetime, "created_at" datetime, "updated_at" datetime, foreign key("bodega_id") references "bodegas"("id") on delete cascade, foreign key("articles_id") references "articles"("id") on delete cascade, foreign key("creado_por_id") references "users"("id") on delete set null, foreign key("confirmado_por_id") references "users"("id") on delete set null);
CREATE TABLE IF NOT EXISTS "variantes" ("id" integer primary key autoincrement not null, "article_id" integer not null, "medida" varchar, "color" varchar, "material" varchar, "calidad" varchar, "created_at" datetime, "updated_at" datetime, foreign key("article_id") references "articles"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "bodega_user" ("id" integer primary key autoincrement not null, "bodega_id" integer not null, "user_id" integer not null, "created_at" datetime, "updated_at" datetime, foreign key("bodega_id") references "bodegas"("id") on delete cascade, foreign key("user_id") references "users"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "users" ("id" integer primary key autoincrement not null, "name" varchar not null, "email" varchar not null, "email_verified_at" datetime, "password" varchar not null, "remember_token" varchar, "created_at" datetime, "updated_at" datetime, "telefono" varchar, "documento_identidad" varchar, "tipo_documento" varchar default ('CC'), "fecha_nacimiento" date, "genero" varchar, "direccion" varchar, "ciudad" varchar, "cargo" varchar, "estado" tinyint(1) not null default ('1'), "bodega_id" integer, "active_bodega_id" integer, foreign key("bodega_id") references bodegas("id") on delete set null on update no action, foreign key("active_bodega_id") references "bodegas"("id"));
CREATE TABLE IF NOT EXISTS "articles" ("id" integer primary key autoincrement not null, "nombre" varchar not null, "tipo" varchar not null, "codigo" varchar, "descripcion" text, "precio" numeric, "unidad_medida" varchar, "imagen" varchar, "estado" tinyint(1) not null default ('1'), "temporada" varchar, "proveedor_id" integer, "created_at" datetime, "updated_at" datetime, "bodega_id" integer, "fecha_caducidad" datetime, "tipo_detalle" varchar, foreign key("proveedor_id") references suppliers("id") on delete cascade on update no action, foreign key("bodega_id") references "bodegas"("id") on delete set null);
CREATE TABLE IF NOT EXISTS "clientes" ("id" integer primary key autoincrement not null, "nombre" varchar not null, "documento_identidad" varchar, "telefono" varchar, "email" varchar, "tiene_credito" tinyint(1) not null default '0', "limite_credito" numeric not null default '0', "user_id" integer, "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id") on delete set null);
CREATE TABLE IF NOT EXISTS "cajas" ("id" integer primary key autoincrement not null, "nombre" varchar not null, "bodega_id" integer not null, "activa" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime, foreign key("bodega_id") references "bodegas"("id"));
CREATE TABLE IF NOT EXISTS "sesion_cajas" ("id" integer primary key autoincrement not null, "caja_id" integer not null, "user_id" integer not null, "monto_inicial" numeric not null, "monto_final_calculado" numeric, "monto_final_contado" numeric, "diferencia" numeric, "fecha_apertura" datetime not null, "fecha_cierre" datetime, "estado" varchar check ("estado" in ('abierta', 'cerrada', 'pendiente_aprobacion', 'aprobada')) not null default 'abierta', "aprobado_por_id" integer, "notas_cierre" text, "created_at" datetime, "updated_at" datetime, foreign key("caja_id") references "cajas"("id"), foreign key("user_id") references "users"("id"), foreign key("aprobado_por_id") references "users"("id"));
CREATE TABLE IF NOT EXISTS "ventas" ("id" integer primary key autoincrement not null, "user_id" integer not null, "bodega_id" integer not null, "cliente_id" integer, "sesion_caja_id" integer not null, "subtotal" numeric not null, "descuento" numeric not null default '0', "total" numeric not null, "metodo_pago" varchar check ("metodo_pago" in ('efectivo', 'transferencia', 'tarjeta', 'credito')) not null, "estado" varchar check ("estado" in ('completada', 'anulada')) not null default 'completada', "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id"), foreign key("bodega_id") references "bodegas"("id"), foreign key("cliente_id") references "clientes"("id"), foreign key("sesion_caja_id") references "sesion_cajas"("id"));
CREATE TABLE IF NOT EXISTS "venta_detalles" ("id" integer primary key autoincrement not null, "venta_id" integer not null, "article_id" integer not null, "cantidad" integer not null, "precio_unitario" numeric not null, "descuento_item" numeric not null default '0', "subtotal_item" numeric not null, "created_at" datetime, "updated_at" datetime, foreign key("venta_id") references "ventas"("id") on delete cascade, foreign key("article_id") references "articles"("id"));
CREATE TABLE IF NOT EXISTS "bodega_article" ("id" integer primary key autoincrement not null, "bodega_id" integer not null, "article_id" integer not null, "stock" integer not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("bodega_id") references "bodegas"("id") on delete cascade, foreign key("article_id") references "articles"("id") on delete cascade);
DELETE FROM sqlite_sequence;
CREATE INDEX "sessions_user_id_index" on "sessions" ("user_id");
CREATE INDEX "sessions_last_activity_index" on "sessions" ("last_activity");
CREATE INDEX "jobs_queue_index" on "jobs" ("queue");
CREATE UNIQUE INDEX "failed_jobs_uuid_unique" on "failed_jobs" ("uuid");
CREATE UNIQUE INDEX "permissions_name_guard_name_unique" on "permissions" ("name", "guard_name");
CREATE UNIQUE INDEX "roles_name_guard_name_unique" on "roles" ("name", "guard_name");
CREATE INDEX "model_has_permissions_model_id_model_type_index" on "model_has_permissions" ("model_id", "model_type");
CREATE INDEX "model_has_roles_model_id_model_type_index" on "model_has_roles" ("model_id", "model_type");
CREATE UNIQUE INDEX "bodegas_nombre_unique" on "bodegas" ("nombre");
CREATE UNIQUE INDEX "users_documento_identidad_unique" on "users" ("documento_identidad");
CREATE UNIQUE INDEX "users_email_unique" on "users" ("email");
CREATE UNIQUE INDEX "clientes_documento_identidad_unique" on "clientes" ("documento_identidad");
CREATE UNIQUE INDEX "clientes_email_unique" on "clientes" ("email");
CREATE UNIQUE INDEX "clientes_user_id_unique" on "clientes" ("user_id");
COMMIT;
